/* Wrapper for yylex() to buffer look-ahead token */
#define UNPUTMAX 10
int unputted_tok[UNPUTMAX];  /* unput token number   */
int unput_tok_count;  /* 1 if token was unput */
YYSTYPE yylval;

char kb_input_new (void);
void kb_unput(char);
int kblex (void);
#define KB_INPUT kb_input_new
